import '../styles/Login.css';
import axios from "axios"
import React, {useState} from "react";
import Profile from './profile';

var response_data;

function App() {
  const [user, setUser] = useState(false);

  const HandleSubmit = (event) => {

    event.preventDefault();
    const email = document.getElementById('email').value
    const password = document.getElementById("userpassword").value
    var config = {
      method: 'post',
      url: 'http://127.0.0.1:8000/api/login/',
      // headers: { 
      //   'Authorization': 'token 5f1beddc2b0ed77c6980b4a113d740c47b548440'
      // },
      data: {"username":email, "password":password}
    };
    axios(config)
    .then(function (response) {
      console.log(response.data);
      response_data=response.data;
      setUser(true);
      <Profile value={response_data} />

    })
    .catch(function (error) {
      console.log(error);
    }); 
  }
  return (
    <div className="container">
      <form className="input-form" >
        <p className="login-header"> Login into the world of table tennis</p> <br />

        <label>Email</label>
        <input type="email" className="login-form-input" value="piyush" name="email" id="email" required/><br /><br />

        <label>Password: </label>
        <input type="password" className="login-form-input" value="123" placeholder="Password" name="password" id="userpassword" required/><br /><br />


        <input type="checkbox" id="remember" />
        <label > Remember me</label>

        <p className="login-forget-password"> Forget Password?</p>
        <button type="submit" className="login-submit-button" onClick={HandleSubmit}>Login</button>
      </form> 
      {user
      ?window.location.href='/profile'
      :console.log("hi from bottom of login page")
      }
      <div>Not a member <Link to="/signup"><u>Signup</u></Link> here</div>

    </div>
  );
}
export default App;
