import '../styles/Login.css';
import axios from "axios"
import React, {useState} from "react";
import Profile from './profile';

var response_data;

function App() {
  const [user, setUser] = useState(false);
  const passwordeye = () =>{
		const togglePassword = document.getElementById('togglePassword')
    const password = document.querySelector('#password')
    togglePassword.addEventListener('click', function (e) {
    const type = password.getAttribute('type') === 'password' ? 'text' : 'password';
    password.setAttribute('type', type);
    this.classList.toggle('fa-eye-slash');
    });
	}

  const HandleSubmit = (event) => {

    event.preventDefault();
    const email = document.getElementById('email').value
    const password = document.getElementById("userpassword").value
    var config = {
      method: 'post',
      url: 'http://127.0.0.1:8000/api/login/',
      // headers: { 
      //   'Authorization': 'token 5f1beddc2b0ed77c6980b4a113d740c47b548440'
      // },
      data: {"username":email, "password":password}
    };
    axios(config)
    .then(function (response) {
      console.log(response.data);
      response_data=response.data;
      setUser(true);
      <Profile value={response_data} />

    })
    .catch(function (error) {
      console.log(error);
    }); 
  }
  return (
    <div className="container">
      <form className="input-form" >
        <p className="login-header"> Login into the world of table tennis</p> <br />

        <label>Email</label>
        <input type="email" className="login-form-input" value="piyush" placeholder="Email" name="email" id="email" required/><br /><br />

        <label>Password: </label>
        <input type="password" className="login-form-input" value="123" name="password" id="password" required/><br /><br />
        <i className="far fa-eye eyelogin" id="togglePassword" onClick={passwordeye}></i>

        <input type="checkbox" id="remember" />
        <label > Remember me</label>

        <p className="login-forget-password"> Forget Password?</p>
        <button type="submit" className="login-submit-button" onClick={HandleSubmit}>Login</button>
      </form> 
      {user
      ?window.location.href='/profile'
      :console.log("hi from bottom of login page")
      }
      <p className="login-signup"> New to Table tennis?<a href="/signup">Signup</a> here</p>
    </div>
  );
}
export default App;
